mapper.update:
update announcement
title = #{title}, content = #{content}, attachment_name = #{attachmentName}, attachment_url = #{attachmentUrl}, update_time = #{updateTime}, is_pinned = #{isPinned},
where announcement_id = #{announcementId}

mapper.delete:
delete from announcement where announcement_id in (

        #{id}

    )

mapper.select:
SELECT announcement.*, user.name creator_name FROM announcement LEFT JOIN user on announcement.creator_id = user.id
AND announcement.announcement_id = #{announcementId}
AND announcement.title LIKE CONCAT('%', #{title}, '%')
AND user.name LIKE CONCAT('%', #{creatorName}, '%')
AND announcement.create_time BETWEEN #{beginDate} AND #{endDate}
