mapper.update:
UPDATE company
# 待后续完善是否多表修改 company_name = #{companyName}, company_intro = #{companyIntro},
WHERE company_id = #{companyId};

mapper.delete:
DELETE FROM company
WHERE company_id IN

    #{id}

mapper.select:
SELECT c.company_id, c.company_name, c.company_intro, u.name hr_name, u.gender, u.email, u.phone FROM company c left join user u on c.user_id = u.id
AND c.company_id = #{companyId}
AND c.company_name LIKE CONCAT('%', #{companyName}, '%')
AND u.name LIKE CONCAT('%', #{hrName}, '%')
AND u.gender = #{gender}
AND u.phone = #{phone}
AND u.email = #{email}
