mapper.update:
UPDATE student
user_id = #{userId}, major_id = #{majorId}, graduation_year = #{graduationYear},
student_id = #{studentId}

mapper.select:
SELECT s.student_id AS studentId, u.name AS name, u.gender AS gender, m.name AS major, d.`name` AS department, s.graduation_year AS graduationYear, s.employment_status AS employmentStatus, j.title AS job_title, jt.`name` AS job_type FROM student_info s LEFT JOIN user u ON s.user_id = u.id LEFT JOIN major m ON s.major_id = m.id LEFT JOIN department d ON m.department_id = d.id LEFT JOIN employment e ON s.student_id = e.student_id LEFT JOIN job j ON e.job_id = j.job_id LEFT JOIN job_type jt ON j.type_id = jt.type_id
AND s.student_id = #{studentId}
AND u.name LIKE CONCAT('%', #{name}, '%')
AND u.gender = #{gender}
AND s.major_id = #{majorId}
AND d.id = #{departmentId}
AND s.graduation_year = #{graduationYear}
AND s.employment_status = #{employmentStatus}
